import _choice from '../kernel/_choice.js';
import randint from './randint.js';

/**
 * Sample a single element from an array.
 *
 * @function
 * @param {Array} a The input array.
 * @param {number} i Inclusive left bound.
 * @param {number} j Non-inclusive right bound.
 * @return {any} The sampled element.
 */
const choice = _choice(randint);
export default choice;

import _randfloat from '../kernel/_randfloat.js';
import random from './random.js';

/**
 * Returns a double in interval [i, j[ (i included, j excluded)
 * according to a uniform distribution.
 *
 * @function
 * @param {number} i The inclusive left bound
 * @param {number} j The non-inclusive right bound
 * @return {number} A double in the interval [i, j[ taken uniformly at
 * random.
 */
const randfloat = _randfloat(random);
export default randfloat;

import _randint from '../kernel/_randint.js';
import random from './random.js';

/**
 * Returns an integer in interval [i, j[ (i included, j excluded)
 * according to a uniform distribution.
 *
 * @function
 * @param {number} i The inclusive left bound
 * @param {number} j The non-inclusive right bound
 * @return {number} An integer in the interval [i, j[ taken uniformly at
 * random.
 */
const randint = _randint(random);
export default randint;

/**
 * Returns a double in the interval [0, 1) using JavaScript Math.random().
 * @return {number}
 */
const random = () => {
	return Math.random();
};

export default random;

import _waterman from '../kernel/_waterman.js';
import randint from './randint.js';

/**
 * Reservoir sampling.
 *
 * @function
 * @param {number} k The size of the sample.
 * @param {Iterable} iterable The input iterable.
 * @param {Array} [output=new Array(k)] The output array.
 * @return {Array} The output array.
 */
const reservoir = _waterman(randint);
export default reservoir;

import _fisheryates from '../kernel/_fisheryates.js';
import randint from './randint.js';

/**
 * Take a sample of size n (without replacement) from the items i through
 * j-1 of the input array. This is done in-place. The sample can be retrieved
 * from position i to i+n.
 *
 * @function
 * @param {number} n The size of the sample.
 * @param {Array} a The input array.
 * @param {number} i The inclusive left bound.
 * @param {number} j The non-inclusive right bound.
 */
const sample = _fisheryates(randint);
export default sample;

import _shuffle from '../kernel/_shuffle.js';
import sample from './sample.js';

/**
 * Shuffle array in-place between positions i and j-1 (inclusive).
 * The other positions are left untouched.
 * @function
 * @param {Array} a The input array.
 * @param {number} i The inclusive left bound.
 * @param {number} j The non-inclusive right bound.
 */
const shuffle = _shuffle(sample);
export default shuffle;

import _fisheryates_inside_out from '../kernel/_fisheryates_inside_out.js';
import randint from './randint.js';

/**
 * Given an input iterable, constructs an array containing the elements of the
 * input shuffled uniformly at random.
 *
 * @function
 * @param {Iterable} iterable The input iterable.
 * @return {Array} The constructed array.
 */
const shuffled = _fisheryates_inside_out(randint);
export default shuffled;

export {default as choice} from './api/choice.js';
export {default as randfloat} from './api/randfloat.js';
export {default as randint} from './api/randint.js';
export {default as random} from './api/random.js';
export {default as randrange} from './api/randrange.js';
export {default as reservoir} from './api/reservoir.js';
export {default as sample} from './api/sample.js';
export {default as shuffle} from './api/shuffle.js';
export {default as shuffled} from './api/shuffled.js';
export {default as _choice} from './kernel/_choice.js';
export {default as _fisheryates} from './kernel/_fisheryates.js';
export {default as _fisheryates_inside_out} from './kernel/_fisheryates_inside_out.js';
export {default as _randfloat} from './kernel/_randfloat.js';
export {default as _randint} from './kernel/_randint.js';
export {default as _randrange} from './kernel/_randrange.js';
export {default as _shuffle} from './kernel/_shuffle.js';
export {default as _waterman} from './kernel/_waterman.js';

/**
 * Builds a choice function given a randint function.
 *
 * @param {Function} randint The randint function.
 * @return {Function} The choice function.
 */
const _choice = (randint) => {
	/**
	 * Sample a single element from an array.
	 *
	 * @param {Array} a The input array.
	 * @param {number} i Inclusive left bound.
	 * @param {number} j Non-inclusive right bound.
	 * @return {any} The sampled element.
	 */
	const choice = (a, i, j) => {
		const x = randint(i, j);
		return a[x];
	};

	return choice;
};

export default _choice;

/**
 * Sample elements from an array using Fisher-Yates method.
 *
 * NOTE: The original description of the algorithm by Fisher and Yates [1] had
 * unnecessary bookkeeping which made the algorithm run in O(n * (j-i)) time.
 * This implementation follows Durstenfeld's [2] and Knuth's [3] descriptions
 * which yield O(n) running time.
 *
 * For more information see the excellent "Fisher–Yates shuffle" page on
 * Wikipedia [4]. Fisher and Yates description is referred there as
 * "Fisher and Yate's original method" or "pencil-and-paper method".
 * The more efficient implementation described by Durstenfeld and Knuth is
 * referred there as "the modern method".
 *
 * [1] Fisher, Ronald A.; Yates, Frank (1938). Statistical tables for
 *     biological, agricultural and medical research.
 * [2] Durstenfeld, R. (July 1964). "Algorithm 235: Random permutation"
 * [3] Knuth, Donald E. (1969). Seminumerical algorithms. The Art of Computer
 *     Programming Volume 2.
 * [4] https://en.wikipedia.org/wiki/Fisher–Yates_shuffle
 *
 * @param {Function} randint The randint function.
 * @return {Function} The sampling function.
 */
const _fisheryates = (randint) => {
	/**
	 * Take a sample of size n (without replacement) from the items i through
	 * j-1 of the input array. This is done in-place. The sample can be
	 * retrieved from position i to i+n.
	 *
	 * @param {number} n The size of the sample.
	 * @param {Array} a The input array.
	 * @param {number} i The inclusive left bound.
	 * @param {number} j The non-inclusive right bound.
	 */
	const sample = (n, a, i, j) => {
		// We will swap at most n elements
		// NOTE: When n = j - i, the last swap swaps a[j-1] with itself,
		// which is a NOOP. /!\ HOWEVER, the last swap is NOT a NOOP when
		// n < j - i. Hence we cannot let k = i + n - 1 in general.
		const k = i + n;

		for (; i < k; ++i) {
			// Choose any index p in the remaining array
			const p = randint(i, j);

			// Swap selected element with the first remaining element.
			const tmp = a[i];
			a[i] = a[p];
			a[p] = tmp;
		}
	};

	return sample;
};

export default _fisheryates;

/**
 * Shuffle elements of an iterable using an inside-out implementation of the
 * Fisher-Yates method.
 *
 * One can observe that if the input contains n elements, the loop has exactly
 * n! possible outcomes: one for the first iteration, two for the second, three
 * for the third, etc., the number of outcomes of a loop being the product of
 * the number of outcomes for each iteration. Given a perfect randint function,
 * each iteration's outcomes are equally likely, and independent of other
 * iterations outcomes.  The proof below shows that these outcomes are
 * distinct.
 *
 * To see that this method yields the correct result (assume perfect randint):
 *   1. Observe that it is correct when the input is empty.
 *   2. By induction:
 *     - Induction hypothesis: assume it is correct when the input consists of
 *       n elements.
 *     - We almost insert the (n+1)th element at one of the n+1 possible
 *       insertion position in the output array. Almost because we move the
 *       element that is at the insertion position at the end instead of
 *       shifting the elements right of the insertion position to make room for
 *       the inserted element.
 *     - Ideally, since we inserted the last element at one of the n+1
 *       positions, we would like that the elements inserted earlier form one
 *       of n! permutations uniformly at random after moving the element under
 *       the insertion position. This is true because the permutations that we
 *       obtain after this move are in one-to-one correspondance with the n!
 *       distinct permutations that can be obtained before the move. These are
 *       equally likely to be produced by the induction hypothesis.
 *
 * @param {Function} randint The randint function.
 * @return {Function} The sampling function.
 */
const _fisheryates_inside_out = (randint) => {
	/**
	 * Given an input iterable, constructs an array containing the elements of
	 * the input shuffled uniformly at random.
	 *
	 * @param {Iterable} iterable The input iterable.
	 * @param {Array} [output=[]] The constructed array.
	 * @return {Array} The constructed array.
	 */
	const shuffled = (iterable, output = []) => {
		let n = 0;
		for (const item of iterable) {
			const i = randint(-1, n);
			if (i === -1) output.push(item);
			else {
				output.push(output[i]);
				output[i] = item;
			}

			++n;
		}

		return output;
	};

	return shuffled;
};

export default _fisheryates_inside_out;

/**
 * Builds a randfloat function given a random number generator.
 *
 * @param {Function} random A function taking no arguments that returns a
 * double uniformly at random in the interval [0, 1).
 *
 * @return {Function} A randfloat function.
 */
const _randfloat = (random) => {
	/**
	 * Returns a double in interval [i, j[ (i included, j excluded)
	 * according to a uniform distribution.
	 *
	 * @param {number} i The inclusive left bound
	 * @param {number} j The non-inclusive right bound
	 * @return {number} A double in the interval [i, j[ taken uniformly at
	 * random.
	 */
	const randfloat = (i, j) => i + random() * (j - i);
	return randfloat;
};

export default _randfloat;

/**
 * Builds a randint function given a random number generator.
 *
 * @param {Function} random A function taking no arguments that returns a
 * double uniformly at random in the interval [0, 1).
 *
 * @return {Function} A randint function.
 */
const _randint = (random) => {
	/**
	 * Returns an integer in interval [i, j[ (i included, j excluded)
	 * according to a uniform distribution.
	 *
	 * @param {number} i The inclusive left bound
	 * @param {number} j The non-inclusive right bound
	 * @return {number} An integer in the interval [i, j[ taken uniformly at
	 * random.
	 */
	const randint = (i, j) => i + Math.floor(random() * (j - i));
	return randint;
};

export default _randint;

/**
 * Builds a randrange function given a randint function.
 *
 * @param {Function} randint The randint function.
 * @return {Function} The choice function.
 */
const _randrange = (randint) => {
	/**
	 * Pick an element from range(start, stop, step) uniformly at random.
	 *
	 * Return an element from range(start, stop, step) selected uniformly at random.
	 * If step is positive, this set corresponds to
	 * {x: x in [start, stop[ AND x % step = 0}.
	 * If step is negative, the range has to be given in reverse order, that is,
	 * largest value first, smallest value second. Both the starting value and the
	 * step value are optional. By default the starting value is <code>0</code>.
	 * The default for the step value is <code>1</code>.
	 *
	 * TODO: Handle empty ranges.
	 *
	 * @param {number} [start=0] - The starting value.
	 * @param {number} stop - The stopping value.
	 * @param {number} [step=1] - The step value.
	 * @return {number} The picked element.
	 */
	const randrange = (start, stop, step) => {
		if (stop === undefined) return randint(0, start);
		if (step === undefined) step = 1;

		if (stop >= start) {
			return start + step * randint(0, Math.floor((stop - start) / step));
		}

		return start + step * randint(0, Math.floor((start - stop) / -step));
	};

	return randrange;
};

export default _randrange;

/**
 * Shuffle the array by sampling the entire array.
 *
 * @param {Function} sample The sample function.
 * @return {Function} The shuffle function.
 */
const _shuffle = (sample) => {
	/**
	 * Shuffle array in-place between positions i and j-1 (inclusive).
	 * The other positions are left untouched.
	 *
	 * @param {Array} a The input array.
	 * @param {number} i The inclusive left bound.
	 * @param {number} j The non-inclusive right bound.
	 */
	const shuffle = (a, i, j) => sample(j - i, a, i, j);
	return shuffle;
};

export default _shuffle;

/**
 * Construct a sampling function using Algorithm R due to Alan Waterman (both
 * name and attribution are due to Knuth).
 *
 * @param {Function} randint The randint function.
 * @return {Function} The sample function.
 */
const _waterman = (randint) => {
	/**
	 * Samples k items uniformly at random from an iterable of unknown size.
	 *
	 * We want each item to have probability k/n of being selected.
	 *
	 * The algorithm works as follows:
	 *   1. We initialize a candidate sample with the first k items.
	 *   2. For each remaining item i, decide whether to insert it in the
	 *   candidate sample with probability k/i, evicting an item from the
	 *   candidate sample at random, or to discard it immediately (with
	 *   probability 1-k/i),
	 *
	 * To prove that the obtained probability of inclusion for each item is correct
	 * we multiply two probabilities:
	 *   1. The probability of entering the candidate sample.
	 *   2. The probability of staying in the candidate sample until the end.
	 *
	 * For items 1 to k, probability 1. is 1, and probability 2. is
	 * (1-1/(k+1))(1-1/(k+2))...(1-1/n)
	 * = (k/(k+1))((k+1)/(k+2))...((n-1)/n) which telescopes to k/n.
	 *
	 * For items i = k+1 to n, where probability 1. is k/i, and probability 2.
	 * is (1-1/(i+1))(1-1/(i+2))...(1-1/n)
	 * = (i/(i+1))((i+1)/(i+2))...((n-1)/n) which telescopes to i/n.
	 *
	 * NOTE: Could also implement so that it yields after each input item.
	 * NOTE: One can reduce the expected number of random bits needed by
	 * avoiding generating any number above k-1:
	 *   - First we branch on whether i < k.
	 *   - Then we generate the random number between 0 and k-1 only if needed.
	 *
	 * To decide on the branch, flip a biased coin with parameter p = k/n.
	 * To do so, flip a fair coin until it differs from the binary
	 * representation of k/n (0.10110101...).
	 * The computation can be made efficient by realizing several things:
	 *   - k is fixed and smaller than n (so divmod step can be skipped)
	 *   - k/(n+1) < k/n (so we can avoid recomputing if the biased flip > k/n)
	 *
	 * This would reduce the number of necessary random bits from O(n log n) to
	 * expected O(n).
	 *
	 * @param {number} k The size of the sample.
	 * @param {Iterable} iterable The input iterable.
	 * @param {Array} [output=new Array(k)] The output array.
	 * @return {Array} The output array.
	 */
	const sample = (k, iterable, output = new Array(k)) => {
		const it = iterable[Symbol.iterator]();

		let n = 0;

		for (; n < k; ++n) {
			const {value, done} = it.next();
			if (done) return output;
			output[n] = value;
		}

		for (; ; ++n) {
			const {value, done} = it.next();
			if (done) return output;
			const i = randint(0, n);
			if (i < k) output[i] = value;
		}
	};

	return sample;
};

export default _waterman;

